import React, { useState, useEffect } from 'react';
import ControleLivro from '../src/controle/ControleLivros';
import ControleEditora from '../src/controle/ControleEditora';
import Livro from './modelo/Livro';



function LivroLista() {
    const livros = ControleLivro.obterLivros()
    return (
        <table className="table table-striped" style={{ fontSize: "14" }}>
            <thead>
                <tr>
                    <th>Título</th>
                    <th>Resumo</th>
                    <th>Editora</th>
                    <th>Autores</th>
                </tr>
            </thead>
            <tbody>
                {livros.map((livro) => (
                    <tr key={livro.codigo}>
                        <td>{livro.titulo}</td>
                        <td>{livro.autor}</td>
                        <td>{livro.editora}</td>
                        <td>
                            <button
                                className="btn btn-danger"
                                onClick={() => ControleLivro.excluir(livro.codigo)}
                            >
                                Excluir
                            </button>
                        </td>
                    </tr>
                ))}
            </tbody>



        </table >

    );
}

export default LivroLista;
