import React, { useState, useEffect } from 'react';
import ControleLivro from '../src/controle/ControleLivros';
import ControladorEditora from '../controladores/ControladorEditora';
import LinhaLivro from './LinhaLivro';

export default function LivroLista() {
  const [livros, setLivros] = useState([]);
  const [carregado, setCarregado] = useState(false);

  useEffect(() => {
    async function obterLivros() {
      const controleLivro = new ControleLivro();
      const dadosLivros = await controleLivro.obterLivros();
      setLivros(dadosLivros);
      setCarregado(true);
    }

    obterLivros();
  }, [carregado]);

  function excluir(codigo) {
    const controleLivro = new ControladorLivro();
    controleLivro.excluirLivro(codigo).then(() => setCarregado(false));
  }

  return (
    <main>
      <h1>Lista de Livros</h1>
      <table>
        <thead>
          <tr>
            <th>Título</th>
            <th>Editora</th>
            <th>Autores</th>
            <th>Ano</th>
            <th>Opções</th>
          </tr>
        </thead>
        <tbody>
          {livros.map((livro) => (
            <LinhaLivro
              key={livro.codigo}
              livro={livro}
              excluir={excluir}
            />
          ))}
        </tbody>
      </table>
    </main>
  );
}
