import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import  ControleLivro from './controle/ControleLivros';
import ControleEditora  from './controle/ControleEditora';


function LivroDados() {
  const { id } = useParams();
  const [livro, setLivro] = useState({ titulo: '', autor: '', codEditora: '' });
  const [editoras, setEditoras] = useState([]);
  const controleLivro = new ControleLivro();
  const controleEditora = new ControleEditora();

  const handleSubmit = (event) => {
    event.preventDefault();
    controleLivro.incluir(livro);
  };

  useEffect(() => {
    const fetchLivro = async () => {
      const dados = await controleLivro.getLivro(id);
      setLivro({ ...dados });
    };
    fetchLivro();
  }, [id, controleLivro]);

  useEffect(() => {
    const fetchEditoras = async () => {
      const dados = await controleEditora.getEditoras();
      const opcoes = dados.map((editora) => ({ value: editora.codEditora, text: editora.nome }));
      setEditoras(opcoes);
    };
    fetchEditoras();
  }, [controleEditora]);

  return (
    <div>
      <h1>Dados do Livro</h1>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formTitulo">
          <Form.Label>Título</Form.Label>
          <Form.Control type="text" placeholder="Digite o título do livro" value={livro.titulo} onChange={(event) => setLivro({ ...livro, titulo: event.target.value })} />
        </Form.Group>
        <Form.Group controlId="formAutor">
          <Form.Label>Autor</Form.Label>
          <Form.Control type="text" placeholder="Digite o nome do autor" value={livro.autor} onChange={(event) => setLivro({ ...livro, autor: event.target.value })} />
        </Form.Group>
        <Form.Group controlId="formEditora">
          <Form.Label>Editora</Form.Label>
          <Form.Control as="select" value={livro.codEditora} onChange={(event) => setLivro({ ...livro, codEditora: event.target.value })}>
            {editoras.map((opcao) => (
              <option key={opcao.value} value={opcao.value}>
                {opcao.text}
              </option>
            ))}
          </Form.Control>
        </Form.Group>
        <Button variant="primary" type="submit">
          Salvar
        </Button>
      </Form>
    </div>
  );
}

export default LivroDados;
