import React, { useState, useEffect } from 'react';
import ControleLivro from '../src/controle/ControleLivros';
import ControladorEditora from '../src/controle/ControleEditora';

function LinhaLivro(props) {
    const { livro, excluir } = props;
    const controleEditora = new ControleEditora();
    const nomeEditora = controleEditora.getNomeEditora(livro.codEditora);

    return (
        <tr>
            <td>
                {livro.titulo}
                <button onClick={() => excluir(livro)}>Excluir</button>

            </td>
            <td>{nomeEditora}</td>
            <td>{livro.ano}</td>
            <td>
                <ul>
                    {livro.autores.map((autor, index) => (
                        <li key={index}>{autor}</li>
                    ))}
                </ul>
            </td>
        </tr>
    );
}


class LivroLista extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            livros: [], // inicializa o estado com uma lista vazia de livros
        };
        this.controleLivro = new ControleLivro();
        this.controleEditora = new ControleEditora();
        this.excluirLivro = this.excluirLivro.bind(this);
    }

    componentDidMount() {
        const livros = this.controleLivro.getLivros();
        this.setState({ livros });
    }

    excluirLivro(livro) {
        this.controleLivro.excluirLivro(livro.id);
        const livros = this.state.livros.filter((l) => l.id !== livro.id);
        this.setState({ livros });
    }

    render() {
        return (
            <table>
                <thead>
                    <tr>
                        <th>Título</th>
                        <th>Editora</th>
                        <th>Resumo</th>
                        <th>Autores</th>
                    </tr>
                </thead>
                <tbody>
                    {this.state.livros.map((livro) => (
                        <LinhaLivro
                            key={livro.id}
                            livro={livro}
                            excluir={this.excluirLivro}
                        />
                    ))}
                </tbody>
            </table>
        );
    }
}

export default LivroLista;
