import React, { useState, useEffect } from 'react';
import ControleLivro from '../src/controle/ControleLivros';
import ControleEditora from '../src/controle/ControleEditora';



function LinhaLivro(props) {
  const { livro, excluir } = props;
  const controleEditora = new ControleEditora();
  const nomeEditora = controleEditora.getNomeEditora(livro.codEditora);

  return (
    <tr>
      <td>
        {livro.titulo}
        <button onClick={() => excluir(livro.id)}>Excluir</button>
      </td>
      <td>{nomeEditora}</td>
      <td>{livro.resumo}</td>
      <td>
        <ul>
          {livro.autores.map((autor, index) => (
            <li key={index}>{autor}</li>
          ))}
        </ul>
      </td>
    </tr>
  );
}

function LivroLista() {
  const [livros, setLivros] = useState([]);
  const [carregado, setCarregado] = useState(false);

  const controleLivro = new ControleLivro();
  const controleEditora = new ControleEditora();

  useEffect(() => {
    if (!carregado) {
      const livros = controleLivro.obterLivros();
      setLivros(livros);
      setCarregado(true);
    }
  }, [carregado]);

  const excluir = (id) => {
    controleLivro.excluirLivro(id);
    setCarregado(false);
  };

  return (
    <main>
      <h1>Lista de Livros</h1>
      <table>
        <thead>
          <tr>
            <th>Título</th>
            <th>Editora</th>
            <th>Resumo</th>
            <th>Autores</th>
          </tr>
        </thead>
        <tbody>
          {livros.map((livro) => (
            <LinhaLivro key={livro.id} livro={livro} excluir={excluir} />
          ))}
        </tbody>
      </table>
    </main>
  );
}

export default LivroLista;
